// Fungsi Format Tanggal
function formatDate(date) {
    const dateObj = new Date(date);
    const jpFormat = `${dateObj.getFullYear()}年${dateObj.getMonth() + 1}月${dateObj.getDate()}日`;
    const idFormat = dateObj.toLocaleDateString("id-ID", { year: "numeric", month: "long", day: "numeric" });

    return { jp: jpFormat, id: idFormat };
}

// Fungsi Penjualan
function formatSalesFirst(sales) {
    if (!sales) return { jp: '', id: '' };

    // Format untuk Indonesia
    const idFormat = `${sales.toLocaleString('id-ID', { useGrouping: true })} eksemplar`;

    // Format untuk Jepang dengan tambahan "冊"
    const man = Math.floor(sales / 10000); // Hitung puluhan ribu (万)
    const remainder = sales % 10000; // Sisa setelah puluhan ribu
    const jpFormat = remainder > 0 ? `${man}万${remainder}冊` : `${man}万冊`;

    return { jp: jpFormat, id: idFormat };
}




// Fungsi untuk membuat photobook
